function envioMensajeOk(){alertify.success("Mensaje enviado correctamente!");nombre.val("");email.val("");tipoMensaje.val(0);mensaje.val("")}function envioMensajeError(){alertify.error("Ha ocurrido un error al enviar el mensaje. Inténtelo nuévamente.")}function validar(){return validarCampoObligatorio(nombre,"Debe ingresar su nombre")&&validarCampoObligatorio(tipoMensaje,"Debe ingresar el tipo de mensaje")&&validarCampoObligatorio(mensaje,"Debe ingresar un mensaje")}function validarCampoObligatorio(n,t){if(n.val()==""&&n.parent().find(".must").length===0){var i=$("<div class='must my-1'>");return i.text(t),i.css("color","red"),i.css("font-size","10px"),n.css("outline","1px solid red"),n.parent().append(i),!1}if(n.val()!="")return n.parent().find(".must").remove(),n.css("outline","none"),!0}var nombre=$("#nombre"),email=$("#email"),tipoMensaje=$("#tipoMensaje"),mensaje=$("#mensaje"),btnEnviar=$("#btnEnviar");btnEnviar.click(function(){if(validar()){var n={};n.nombre=nombre.val();n.email=email.val()===""?null:email.val();n.tipoMensaje=tipoMensaje.val();n.mensaje=mensaje.val();alertify.message("Cargando...");$.ajax({url:window.pathEnviarMensaje,data:JSON.stringify(n),success:envioMensajeOk,type:"POST",error:envioMensajeError,dataType:"json",contentType:"application/json; charset=utf-8"})}});$(".carousel").slick({dots:!0,infinite:!0,speed:500,fade:!0,autoplay:!0,cssEase:"linear"});